def is_samepatterns(colors, patterns):
    if len(colors) != len(patterns):
        return False
    color_map = {}
    pattern_map = {}
    for color, pattern in zip(colors, patterns):
        if color in color_map and color_map[color] != pattern:
            return False
        if pattern in pattern_map and pattern_map[pattern] != color:
            return False
        color_map[color] = pattern
        pattern_map[pattern] = color
    return True

