def odd_Equivalent(s, n):
    def rotate(s, n):
        return s[n:] + s[:n]

    def count_odd(s):
        return sum(int(x) % 2 for x in s)

    count = 0
    for _ in range(n):
        s = rotate(s, 1)
        count += count_odd(s)

    return count

assert odd_Equivalent("011001", 6) == 3

