def search(nums):
    left, right = 0, len(nums) - 1
    while left < right:
        mid = (left + right) // 2
        if nums[mid] != nums[mid - 1] and nums[mid] != nums[mid + 1]:
            return nums[mid]
        elif nums[mid] == nums[mid - 1]:
            if mid % 2 == 0:
                right = mid - 2
            else:
                left = mid + 1
        else:
            if mid % 2 == 0:
                left = mid + 2
            else:
                right = mid - 1
    return nums[left]

