def max_sum_increasing_subseq(arr, i, k, j):
    if i == 0:
        return arr[0]
    if i == 1:
        return max(arr[0], arr[1])

    dp = [0] * (i + 1)
    dp[0] = arr[0]
    dp[1] = max(arr[0], arr[1])

    for index in range(2, i + 1):
        dp[index] = max(dp[index - 1], dp[index - 2] + arr[index - 1])

    max_sum = dp[i]

    for index in range(i + 1, j + 1):
        if arr[index] > arr[k]:
            max_sum = max(max_sum, dp[i] + arr[index])

    return max_sum

