def count_Substrings(s):
    count = 0
    for i in range(len(s)):
        sum_digits = 0
        for j in range(i, len(s)):
            sum_digits += int(s[j])
            if sum_digits == j - i + 1:
                count += 1
    return count

assert count_Substrings('112112') == 6

def count_Substrings(s):
    count = 0
    for i in range(len(s)):
        for j in range(i+1, len(s)+1):
            substring = s[i:j]
            if sum(int(digit) for digit in substring) == len(substring):
                count += 1
    return count

