def sum_digits(n, reverse=False):
    digits = [int(digit) for digit in str(abs(n))]
    if reverse:
        digits.reverse()
    return sum(digits)

def test_sum_digits(test_cases):
    results = []
    for test_case in test_cases:
        input_num, reverse, expected_output = test_case
        result = sum_digits(input_num, reverse)
        results.append(result == expected_output)
    return results

