def check_min_heap(arr):
    n = len(arr)
    for i in range(n//2):
        if 2*i + 1 < n and arr[i] > arr[2*i + 1]:
            return False
        if 2*i + 2 < n and arr[i] > arr[2*i + 2]:
            return False
    return True

assert check_min_heap([1, 2, 3, 4, 5, 6]) == True

