def last(arr, target):
    start, end = 0, len(arr) - 1
    result = -1

    while start <= end:
        mid = (start + end) // 2
        if arr[mid] == target:
            result = mid
            start = mid + 1
        elif arr[mid] < target:
            start = mid + 1
        else:
            end = mid - 1

    return result

